let timing = get("g_config").transition.decimation;
timing = timing ? timing : 30;
let duration = get("color_transition")*1000 //returns time in seconds, cnv2 ms
let stepCount = Math.floor(duration/timing)
// console.error("" + timing + ", " + duration + ", " + stepCount)
let state = get(this);
let pgm = state //incoming new pgm
let pvw = get("g_take_sidestore") // outgoing previous program / new pvw
pvw = pvw ? pvw : ["#FF0000","#00FF00","#0000FF"]

function hexToRGB(hex) { // thank you to Tim Down for this function and its twin below.
  let result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex)
  return result ? [parseInt(result[1], 16),parseInt(result[2], 16),parseInt(result[3], 16)] : null;
}
function rgbToHex(r,g,b) {
  r=Math.round(r);
  g=Math.round(g);
  b=Math.round(b);
  return "#" + (1<< 24 | r << 16 | g << 8 | b).toString(16).slice(1)
}

for (let i=0; i<=stepCount; i++) {
  let stepArr = []
  for (let col in pgm) {
    let pgmRGB = hexToRGB(pgm[col])
    let pvwRGB = hexToRGB(pvw[col])
    let step = []
    for (let c = 0; c<3; c++) {
      step[c] = pvwRGB[c]+((pgmRGB[c]-pvwRGB[c])/stepCount)*i
      step[c] = stepCount ? step[c] : pgmRGB[c]
    }
    stepArr.push(step)
  }
  let outArr = []
  for (let col in stepArr) {
    outArr[col] = rgbToHex(stepArr[col][0],stepArr[col][1],stepArr[col][2])
  }
  // console.error(""+i+", " + outArr)
  for (let group in outArr) {
    setTimeout(Math.random(),()=>{
    send(null,'/5s/shownet/color/pgm/group/'+group+'/color', outArr[group])
    // console.error("out: "+i)
  },timing*i+(group/3*timing))
}
