widgets.onValue:
setTimeout(null,() => {
  set('g_take_sidestore', get('col_pgm_state'));
    setTimeout(null,() => {
      set('col_pgm_state', get("col_pvw_state"));
      setTimeout(null,() => {
        set("col_pvw_state", get("g_take_sidestore"));
        setTimeout(null,() => {
          set('g_take_sidestore', get("col_pgm_groups_active"));
          setTimeout(null,() => {
            set('col_pgm_groups_active', get("col_pvw_groups_active"));
            setTimeout(null,() => {
              set("col_pvw_groups_active", get("g_take_sidestore"));
              setTimeout(null,() => {
                set("g_take_sidestore",null)
                set("g_take_bcast_pgm",get('col_pgm_state'))
              }, 50);
            }, 10);
          }, 10);
        }, 30);
      }, 10);
    }, 10);
  }, 0);

let state = get('col_pgm_state');
for (let group in state) {
  setTimeout('bcast'+group,()=>{
    send(null,'/5s/shownet/color/pgm/group/'+group+'/color', state[group])
  },20*group)
}
